import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [atsScore, setAtsScore] = useState(null);
  const [suggestions, setSuggestions] = useState(null);
  const [applications, setApplications] = useState({});
  const [selectedCategory, setSelectedCategory] = useState('software');
  const [applicationStatus, setApplicationStatus] = useState({});
  const [location, setLocation] = useState('');
  const [includeRemote, setIncludeRemote] = useState(true);
  const [country, setCountry] = useState('us');

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async (event) => {
    event.preventDefault();
    if (!file) return;

    const formData = new FormData();
    formData.append('resume', file);
    formData.append('jobCategory', selectedCategory);
    formData.append('location', location);
    formData.append('includeRemote', includeRemote);
    formData.append('country', country);

    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/upload-resume', formData);
      setJobs(response.data.jobs);
      setAtsScore(response.data.atsScore);
      setSuggestions(response.data.suggestions);
    } catch (error) {
      console.error('Upload failed:', error);
      alert('Upload failed: ' + error.message);
    }
    setLoading(false);
  };

  const handleQuickApply = (job) => {
    if (job.redirect_url) {
      window.open(job.redirect_url, '_blank');
    } else {
      alert('Application URL not available');
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Job Matchmaker</h1>
      </header>

      <main>
        <section className="upload-section">
          <form onSubmit={handleUpload}>
            <div className="category-select">
              <label htmlFor="category">Select Job Category: </label>
              <select
                id="category"
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                <option value="software">Software</option>
                <option value="marketing">Marketing</option>
                <option value="office_admin">Office Admin</option>
                <option value="engineering">Engineering</option>
                <option value="medical">Medical</option>
                <option value="cleaning">Cleaning</option>
                <option value="handyman">Handyman</option>
                <option value="construction">Construction</option>
                <option value="customer_service">Customer Service</option>
                <option value="accounting">Accounting</option>
                <option value="hospitality">Hospitality</option>
              </select>
            </div>

            <div className="location-filters">
              <div className="country-select">
                <label htmlFor="country">Country: </label>
                <select
                  id="country"
                  value={country}
                  onChange={(e) => setCountry(e.target.value)}
                >
                  <option value="us">United States</option>
                  <option value="uk">United Kingdom</option>
                  <option value="ca">Canada</option>
                  <option value="au">Australia</option>
                </select>
              </div>

              <div className="location-input">
                <label htmlFor="location">City/State: </label>
                <input
                  type="text"
                  id="location"
                  value={location}
                  onChange={(e) => setLocation(e.target.value)}
                  placeholder="e.g., New York, NY"
                />
              </div>

              <div className="remote-toggle">
                <label>
                  <input
                    type="checkbox"
                    checked={includeRemote}
                    onChange={(e) => setIncludeRemote(e.target.checked)}
                  />
                  Include Remote Jobs
                </label>
              </div>
            </div>

            <input type="file" onChange={handleFileChange} accept=".pdf" />
            <button type="submit" disabled={!file || loading}>
              {loading ? 'Uploading...' : 'Upload Resume'}
            </button>
          </form>
        </section>

        {atsScore && (
          <section className="ats-section">
            <h2>ATS Score: {atsScore}%</h2>
            <div className="suggestions">
              <h3>Suggestions for Improvement:</h3>
              <p>{suggestions}</p>
            </div>
          </section>
        )}

        <section className="jobs-section">
          {jobs.length === 0 && !loading && (
            <div className="no-jobs-message">
              No jobs found. Try adjusting your location filters or enabling remote jobs.
            </div>
          )}

          {jobs.map(job => (
            <div key={job.id} className="job-card">
              <h3>{job.title}</h3>
              <p className="company">{job.company}</p>
              <p className="location">
                {job.is_remote ? 'üåê Remote' : `üìç ${job.location}`}
              </p>
              {job.salary_min && job.salary_max && (
                <p className="salary">
                  ${job.salary_min.toLocaleString()} - ${job.salary_max.toLocaleString()}
                </p>
              )}
              <button 
                onClick={() => handleQuickApply(job)}
                className="quick-apply-button"
              >
                Quick Apply
              </button>
            </div>
          ))}
        </section>
      </main>
    </div>
  );
}

export default App;
