import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [atsScore, setAtsScore] = useState(null);
  const [suggestions, setSuggestions] = useState(null);
  const [applications, setApplications] = useState({});
  const [selectedCategory, setSelectedCategory] = useState('software');
  const [applicationStatus, setApplicationStatus] = useState({});

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async (event) => {
    event.preventDefault();
    if (!file) return;

    const formData = new FormData();
    formData.append('resume', file);
    formData.append('jobCategory', selectedCategory);

    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/upload-resume', formData);
      setJobs(response.data.jobs);
      setAtsScore(response.data.atsScore);
      setSuggestions(response.data.suggestions);
    } catch (error) {
      console.error('Upload failed:', error);
      alert('Upload failed: ' + error.message);
    }
    setLoading(false);
  };

  const handleQuickApply = async (job) => {
    try {
      if (!file) {
        alert('Please upload a resume first');
        return;
      }

      const formData = new FormData();
      formData.append('resume', file);

      const jobData = {
        id: job.id,
        title: job.title,
        company: job.company,
        redirect_url: job.redirect_url,
        category: job.category
      };

      const response = await axios.post('http://localhost:5000/apply-job', {
        jobData,
        resumeData: file
      });

      setApplicationStatus(prev => ({
        ...prev,
        [job.id]: 'applied'
      }));

      if (response.data.applicationUrl) {
        window.open(response.data.applicationUrl, '_blank');
      }

      alert('Application submitted successfully!');
    } catch (error) {
      console.error('Application failed:', error);
      alert('Failed to apply: ' + error.message);
      setApplicationStatus(prev => ({
        ...prev,
        [job.id]: 'failed'
      }));
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Job Matchmaker</h1>
      </header>

      <main>
        <section className="upload-section">
          <form onSubmit={handleUpload}>
            <div className="category-select">
              <label htmlFor="category">Select Job Category: </label>
              <select
                id="category"
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                <option value="software">Software</option>
                <option value="marketing">Marketing</option>
                <option value="office_admin">Office Admin</option>
                <option value="engineering">Engineering</option>
                <option value="medical">Medical</option>
                <option value="cleaning">Cleaning</option>
                <option value="handyman">Handyman</option>
                <option value="construction">Construction</option>
                <option value="customer_service">Customer Service</option>
                <option value="accounting">Accounting</option>
                <option value="hospitality">Hospitality</option>
              </select>
            </div>

            <input type="file" onChange={handleFileChange} accept=".pdf" />
            <button type="submit" disabled={!file || loading}>
              {loading ? 'Uploading...' : 'Upload Resume'}
            </button>
          </form>
        </section>

        {atsScore && (
          <section className="ats-section">
            <h2>ATS Score: {atsScore}%</h2>
            <div className="suggestions">
              <h3>Suggestions for Improvement:</h3>
              <p>{suggestions}</p>
            </div>
          </section>
        )}

        <section className="jobs-section">
          {jobs.map(job => (
            <div key={job.id} className="job-card">
              <h3>{job.title}</h3>
              <p className="company">{job.company}</p>
              <p className="location">{job.location}</p>
              {job.salary_min && job.salary_max && (
                <p className="salary">
                  ${job.salary_min.toLocaleString()} - ${job.salary_max.toLocaleString()}
                </p>
              )}
              <button 
                onClick={() => handleQuickApply(job)}
                disabled={applicationStatus[job.id] === 'applied'}
                className={`quick-apply-button ${applicationStatus[job.id] === 'applied' ? 'applied' : ''}`}
              >
                {applicationStatus[job.id] === 'applied' ? 'Applied' : 'Quick Apply'}
              </button>
            </div>
          ))}
        </section>
      </main>
    </div>
  );
}

export default App;
